import React from 'react';
import { withStyles } from "@material-ui/styles";
import { Icon } from '@material-ui/core';
import Colors from "../Constant/Colors";
import Fonts from "../Constant/Fonts";


const styles = {
  link: {
    textDecoration: "none",
    color: Colors.black1
  },

  container: {
    width: "100%",
    height: "260px",
    margin: "0px auto",
    position: "relative",
    borderRadius: "8px",
    boxSizing: "border-box",
    background: "white",
    overflow: "hidden"
  },
  content: {
    boxSizing: "border-box",
    height: "100%",
    paddingLeft: "10%",
    position: "absolute",
    display: "flex",
    justifyContent: "center",
    flexDirection: "column",
  },
  h1: {
    ...Fonts.h1
  },
  p1: {
    ...Fonts.p1
  },
  blurBox:{
   width:"30%",
   height:"100%",
   background:Colors.greybg,
   position: "absolute",
   boxShadow:`200px 0px 85px 87px ${Colors.greybg}`
  },
  bgImage: {
    position: "absolute",
    width: "100%",
    top: "0px",
    left: "100px",
  },
  iconContainer: {
    paddingTop: "8px",
    display:"flex",
    alignItems:"center"
  }
}

function Card2(props) {
  const { classes, title, intro,linkTo } = props;
  return (
    <a className={classes.link} href={linkTo} target="_blank">


        <div className={classes.container}>

          <img className={classes.bgImage} src="" />

          <div className={classes.blurBox}></div>

          <div className={classes.content}>
            <h1 className={classes.h1}>{title}</h1>

            <div className={classes.iconContainer}>
              <p className={classes.p1}>{intro}</p>
              <Icon
                className="fas fa-external-link-alt"
                style={{ color: "black", fontSize: "14px", paddingLeft:"24px",opacity:"0.6" }}
              />
            </div>
          </div>

        </div>


    </a>
  );
}

export default withStyles(styles)(Card2);
