import Colors from "../Constant/Colors";
import Fonts from "../Constant/Fonts";

export default {
  root: {
    background: Colors.black2,
    boxSizing: "border-box"
  },
  container: {
    width: "980px",
    height: "40vh",
    margin: "0px auto",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    boxSizing: "border-box"
  },

  h1: {
    ...Fonts.s1,
    color: "white",
    textAlign: "center"
  },

  p1: {
    ...Fonts.h2,
    color: Colors.ongrey,
    // paddingTop: "24px",
    textAlign: "center"
  },

  socialMedias: {
    paddingTop: "24px",
    display: "flex",
    alignItems: "center",
  },
  link: {
    opacity: "0.5",
    "&:hover": {
      opacity: "1",
      transition: "0.3s ease-in-out"
    }
  },
}
