import React from 'react'
import {withStyles} from "@material-ui/styles"

//Define Styles ↓
const styles={
  container:{},
h1:{
  fontSize:"100px",
  fontWeight:"900"
}
}
function Hero(props){
  const {classes,slogan,content} = props
  return (
    <div className={classes.container}>
      <h1 className={classes.h1}>{slogan}</h1>
      <p>{content}</p>
    </div>
  )
}

export default withStyles(styles)(Hero);
