import React from 'react';
import logo from "../Constant/logo.svg";
import { Link, NavLink } from "react-router-dom";
import { withStyles } from '@material-ui/styles';

// Define Styles here ↓
const styles = {
  root: {
    position: "sticky",
    top: "0",
    boxSizing: "border-box",
    background: 'black',
    width: "100%",
    height: "60px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "0px 10%",
    borderBotton:"1px solid ragb(255,255,255,0.4)",
    zIndex:"20"
  },
  container: {
    width: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  },
  logo: {
    width: "18px",
    height: "100%"
  },
  routeContainer: {
    width: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
  },
  link: {
    color: "white",
    textDecoration: "none",
    opacity: "0.6",
    fontWeight: "700",
    paddingLeft: "32px",
    '&:hover': {
      opacity: "1",
      transition: "0.2s ease-in-out",
      transform: "scales(1.05)"
    }
  }
};

function NavBar(props) {
  const { classes } = props
  return (
    <nav className={classes.root}>
      <div className={classes.container}>

        <Link to="/">
          <img src={logo} className={classes.logo} />
        </Link>

        <div className={classes.routeContainer}>
          <NavLink className={classes.link} to="/">UX Projects</NavLink>
          <NavLink className={classes.link} to="/projectDetail/:projectId">Open Design</NavLink>
          <NavLink className={classes.link} to="/about">About</NavLink>
        </div>

        <div>
          <Link className={classes.link}>中文</Link>
        </div>
      </div>
    </nav>
  );
};

export default withStyles(styles)(NavBar);
