    zIndex: "20",
    opacity: "0",

    position: "absolute",
    top: "0px",
    left: "0px",

    width: "100%",
    height: "100vh",

    background: "black",
    display: "flex",
    alignItems: "center",

    "& img": {
      width: "100%"
    },

