import React, { Component } from 'react'
import { withStyles } from "@material-ui/styles"
//
import Hero from "../Components/Hero"
import Card from "../Components/Card";
import SmallCard from "../Components/SmallCard";
import Footer from "../Components/Footer";
//
import projects from "../projectDb";

const styles = {
  gridContainer: {
    display: "grid",
    gridTemplateColumns: "auto auto auto",
    gridColumnGap: "16px",
    gridRowGap: "16px",
    padding: "48px 10%",
    background: "black",
  },
  wrapper:{
    padding:"0px 15%",
    background:"black"
  }
}

function HomePage(props) {
  console.log(projects);
  const { classes } = props;
  return (
    <div>
      <Hero
        slogan="Bring Idea to Real"
        content="Full-stack product designer with front-end skills, HTML, CSS, JavaScript,React, and React Native."
      />

      {/* ->Feed1-Fullwitdh Card */}
      <Card title="This is Title" intro="This is intro for the project. It will tell users what the project is" />

      {/* Feed2 */}

      <div className={classes.gridContainer}>
        {projects.map((p) => <SmallCard title={p.projectName} intro={p.projectIntro} key={p.projectName}
        />)}
      </div>
      {/* -> Footer */}
      <div className={classes.wrapper}>
        <Footer />
      </div>

    </div>
  );
}

export default withStyles(styles)(HomePage);
