import React from 'react';
import logo from "../Constant/logo.svg";
import { Link, NavLink } from "react-router-dom";
import { withStyles } from '@material-ui/styles';
import Colors from "../Constant/Colors";
import Fonts from "../Constant/Fonts";
import Icon from "@material-ui/core/Icon";

// Define Styles here ↓
const styles = {
  root: {
    position: "sticky",
    top: "0",
    boxSizing: "border-box",
    background: Colors.black2,
    width: "100%",
    height: "60px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "0px 10%",
    borderBottom: "1px solid rgba(255,255,255,0.05)",
    zIndex: "20"
  },
  container: {
    width: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  },
  logo: {
    width: "20px",
    height: "100%"
  },
  routeContainer: {
    // width: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
  },
  link: {
    color: "white",
    textDecoration: "none",
    opacity: "0.6",
    ...Fonts.p2,
    paddingLeft: "32px",
    display: "flex",
    justifyContent: "center",
    textAlign: "center",
    '&:hover': {
      opacity: "1",
      transition: "0.2s ease-in-out",
      transform: "scales(1.05)",
      cursor: "pointer"
    }
  }
};

function NavBar(props) {
  const { classes, isHome, isDetail, isTranslate } = props
  return (
    <nav className={classes.root}>
      <div className={classes.container}>

        <Link to="/">
          <img src={logo} className={classes.logo} />
        </Link>

        {isHome &&
          <div className={classes.routeContainer}>
            <NavLink className={classes.link} to="/">UX Projects</NavLink>
            <NavLink className={classes.link} to="/projectDetail/:projectId">Open Design</NavLink>
            <NavLink className={classes.link} to="/about">About</NavLink>
          </div>
        }

        {/* {isTranslate && <div>
          <a className={classes.link}>
            中文
          </a>
        </div>} */}

        {isDetail &&
          <div className={classes.routeContainer}>
            <NavLink className={classes.link} to="/">

              <Icon
                className="fas fa-chevron-left"
                style={{ color: "white", paddingRight: "8px" ,width:"5px"}}
              />
              <span>Back</span>
            </NavLink>
          </div>
        }

      </div>
    </nav>
  );
};

export default withStyles(styles)(NavBar);
