import React from 'react';
import { withStyles } from "@material-ui/styles";
import ChipsRow from './ChipsRow';
import TeamMemberRow from "./TeamMemberRow";
//
import Fonts from "../Constant/Fonts";

const styles = {
  fullWidthWrapper: {
    padding: "0px 48px",
    width: "100%",
    boxSizing: "border-box",

    display: "flex",
    flexDirection:"row",
    flexWrap:"wrap",
    justifyContent: "space-between"
  },

  leftWrapper: {
    width: "60%"
  },

  p1: {
    // marginTop: "2em",
    // letterSpacing: "-0.003em",
    // lineHeight: "32px",
    // fontWeight: "400",
    // fontSize: "18px",
  },

  rightWrapper: {
    width: "30%"
  }

};

function ProjectIntro(props) {

  const { classes } = props;
  return (
    <div className={classes.fullWidthWrapper}>

      <div className={classes.leftWrapper}>
        <p className={classes.p1}>
          Cash on Delivery is a prevailing payment method in South East Asia (SEA). I designed a new payment option, Cash on Delivery, for the Wish app in order to increase the international audience in Malaysia.
          We want to test that if the Cash on Delivery help generate more GMV and converting more unique buyer from the SEA market.
          </p>

        <ChipsRow />
      </div>

      <div className={classes.rightWrapper}>
        <TeamMemberRow />
      </div>

    </div>
  )
};

export default withStyles(styles)(ProjectIntro);
