import React, { useState } from 'react'
import { withStyles } from "@material-ui/styles";
//
import Colors from "../Constant/Colors";

const styles = {
  root: {
    width: "100%",
    height: "80%",
    borderRadius: "8px",
    overflow: "hidden",
    background: "none",
    marginTop: "48px",
    alignItems: "center",
    justifyContent: "center",
    "&:hover": {
      cursor: "zoom-in"
    }
  },
  image: {
    width: "100%",
  },
  imageEnlarge: {
    position: "sticky",
    width: "100%",
    height: "100vh",
    top: "0px",
    left: "0px",
    zIndex: "20",
    background: "black",
    display: "flex",
    alignItems: "center",
    "& img": {
      width: "100%",
    }
  }

};

function ExpandableImage(props) {

  const [exapand, setExpand] = useState(false);
  const { classes, image } = props;


  return (
    <div className={classes.root}>
      <img 
      className={classes.image} 
      src={image} 
      onClick={()=>setExpand(true)}
      />

      {exapand && <div 
      className={classes.imageEnlarge}
      >
        <img src={image} />
      </div>}
    </div>

  )
}

export default withStyles(styles)(ExpandableImage);
