import React from 'react';
import { withStyles } from "@material-ui/styles";
import ChipsRow from '../Components/ChipsRow';
import TeamMemberRow from "../Components/TeamMemberRow";


const styles = {
  root: {
    background: "green",
    margin: "40px 10%",
    borderRadius: "8px",
    overflow: "hidden",
    border: "1px solid black"
  },
  heroImageContainer: {
    width: "100%",
    height: "60vh",
    background: "grey"
  },
  fullWidthWrapper: {
    padding: "0px 48px",
    paddingTop: "24px",
    width: "100%",
    background: "white",
    boxSizing: "border-box",
    border: "1px solid black",
  },
  halfSizeContainer: {
    boxSizing: "border-box",
    border: "1px solid green",
    width: "50%",
  }
}

function ProjectDetailPage(props) {
  const { classes, title } = props;
  return (
    <div className={classes.root}>

      <div className={classes.heroImageContainer}>
        this.props.image
      </div>

      <div className={classes.contentWrapper}>
        <h1>Cash On Delivery</h1>
      </div>

      <div className={classes.contentWrapper}>

        <div className={classes.halfSizeContainer}>
          <div>
            <p>Cash On delivery is an new payment option that issued for the SEA market........................................................................</p>

            <div className={classes.metrics}>
              <ChipsRow />
            </div>
          </div>
        </div>

        <div className={classes.halfSizeContainer}>
          <TeamMemberRow />
        </div>



      </div>

      <div>
        <div>Teams</div>
        <div><a>PM</a><a>PM</a></div>
      </div>

      <div>
        <div>this.props.problem and ex-experience</div>
      </div>

    </div>
  );

}

export default withStyles(styles)(ProjectDetailPage);
