import React from 'react';
import { withStyles } from "@material-ui/styles";
import { Link } from "react-router-dom";
import Fonts from "../Constant/Fonts";
import Colors from "../Constant/Colors";


const styles = {
  link: {
    textDecoration: "none",
    color: Colors.black1,
  },
  root: {
    position: "relative",
    width: "100%",
    height: "100%",
    background: Colors.grey3,
    borderRadius: "16px",
    boxSizing: "border-box",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",

    overflow: "hidden",
    '&:hover': {
      transform: "translateY(-10px)",
      transition: "0.3s ease-in-out",
      boxShadow: `0px 6px 29px 8px ${Colors.black1}`,
      cursor: "pointer",
    }
  },

  content: {
    boxSizing: "border-box",
    padding: "32px",
    margin:"0px"
  },

  h2: {
    ...Fonts.h2
  },
  p2: {
    paddingTop: "8px",
    ...Fonts.p2
  },
  program:{
    paddingTop: "8px",
    ...Fonts.p2,
    color:props=> props.backgroundColor
  },
  imageContainer: {
    width: "100%",
    height:"340px",
    overflow:"hidden"
  },
  frontImage: {
    width: "100%",
  }
}

function SmallCard(props) {
  const { classes, title, intro, frontImage, backgroundColor,program } = props;
  return (
    <Link to={`/projectDetail/${title}`} className={classes.link}>
      <div className={classes.root}>

        <div className={classes.imageContainer}>
          <img className={classes.frontImage} src={frontImage} />
        </div>

        <div className={classes.content}>
          <p className={classes.program}>{program}</p>
          <h2 className={classes.h2}>{title}</h2>
          <p className={classes.p2}>{intro}</p>
        </div>
      </div>
    </Link>
  )
};

export default withStyles(styles)(SmallCard);
