import React from 'react'
import { withStyles } from "@material-ui/styles";

//
import ExpandableImage from "./ExpandableImage";
//Colors and Fonts
import Colors from "../Constant/Colors";
import Fonts from "../Constant/Fonts";

const styles = {
  root: {
    padding: "48px 64px",
    marginTop: "48px",
    boxSizing: "border-box",
    borderTop: `1px solid ${Colors.grey3}`,
  },
  wrapper: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "flex-end",
  },
  subTitle: {
    ...Fonts.l2,
    width: "300px",
  },
  p1: {
    ...Fonts.p1,
    width: "680px",
  },
  overFlow: {
    marginTop:"20px",
    width: "100%",
    height: "680px",
    border: `1px solid ${Colors.grey3}`,
    borderRadius: "8px",
    overflow: "hidden"
  },
  overflowP1:{
    ...Fonts.p3,
    color:Colors.black2,
    textAlign:"center"
  },
  overflowContainer:{
    marginTop:"48px"
  }
};

function ProjectContent(props) {
  const { classes, subTitle, copy, url, isImage } = props;
  return (
    <div className={classes.root}>

      <div className={classes.wrapper}>
        <h2 className={classes.subTitle}>{subTitle}</h2>
        <p className={classes.p1}>{copy}</p>
      </div>

      {isImage === "true" ? <ExpandableImage image={url} /> : <div className={classes.overflowContainer}>
      <p className={classes.overflowP1}>(interact with the flow)</p>
      <iframe
        className={classes.overFlow}
        src={url}
        scrolling="no"
        allowfullscreen
      />
      </div>
      }


    </div>
  )
}


export default withStyles(styles)(ProjectContent)
