import React from 'react';
import { withStyles } from "@material-ui/styles";
import { Link } from "react-router-dom";
import Fonts from "../Constant/Fonts";
import Colors from "../Constant/Colors";


const styles = {
  link: {
    textDecoration: "none",
    color: Colors.black1,
  },
  root: {
    position: "relative",
    width: "100%",
    height: "460px",
    background: props=> props.backgroundColor,
    borderRadius: "16px",
    boxSizing: "border-box",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",

    overflow: "hidden",
    '&:hover': {
      transform: "translateY(-10px)",
      transition: "0.3s ease-in-out",
      boxShadow: `0px 6px 29px 8px ${Colors.black1}`,
      cursor: "pointer",
    }
  },

  content: {
    boxSizing: "border-box",
    padding: "32px",
    paddingBotton: "64px",
    margin: "0px",
    position: "relative",
    zIndex:"20",
  },

  h2: {
    ...Fonts.h2,
    color:"white"
  },
  p2: {
    paddingTop: "8px",
    ...Fonts.p2,
    color:"white"
  },
  program: {
    paddingBottom: "8px",
    ...Fonts.p2,
    color: props => props.backgroundColor
  },

  imageContainer: {
    width: "100%",
    height: "340px",
    // backgroundColor: props => props.backgroundColor,
    position: "absolute",
    bottom:"0px",
  },
  frontImage: {
    width: "100%",
  },
  logoContainer: {
    width: "40px",
    height: "40px",
    overflow: "hidden",
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    position: "absolute",
    top: "-20px",
    // background: "white"
  },
  logo: {
    width: "26px"
  }
}

function SmallCard(props) {
  const { classes, title, intro, frontImage, program } = props;
  return (
    <Link to={`/projectDetail/${title}`} className={classes.link}>
      <div className={classes.root}>

        <div className={classes.content}>
          <div className={classes.logoContainer}>
            <img className={classes.logo} src={program} />
          </div>
          <h2 className={classes.h2}>{title}</h2>
          <p className={classes.p2}>{intro}</p>
        </div>

        <div className={classes.imageContainer}>
          <img className={classes.frontImage} src={frontImage} />
        </div>
      </div>
    </Link>
  )
};

export default withStyles(styles)(SmallCard);
