import React from 'react';
import { withStyles } from "@material-ui/styles";
import { Icon } from '@material-ui/core';
import Colors from "../Constant/Colors";
import Fonts from "../Constant/Fonts";


const styles = {
  link: {
    marginTop: "40px",
    textDecoration: "none",
    color: Colors.black1,
  },

  container: {
    width: "100%",
    height: "210px",
    margin: "0px auto",
    position: "relative",
    borderRadius: "8px",
    boxSizing: "border-box",
    background: "white",
    overflow: "hidden",
    "&:hover":{
      "&:iconRoundContainer":{
        boxShadow: "0px 0px 13px 0px rgba(0, 0, 0, 0.2)"
      }
    }
  },
  content: {
    boxSizing: "border-box",
    height: "100%",
    paddingLeft: "10%",
    position: "absolute",
    display: "flex",
    justifyContent: "center",
    flexDirection: "column",
  },
  h1: {
    ...Fonts.h2
  },
  p1: {
    ...Fonts.p1
  },
  blurBox: {
    width: "30%",
    height: "100%",
    background: Colors.greybg,
    position: "absolute",
    boxShadow: `200px 0px 85px 87px ${Colors.greybg}`
  },
  bgImage: {
    position: "absolute",
    width: "100%",
    top: "0px",
    left: "100px",
  },
  iconContainer: {
    paddingTop: "8px",
    display: "flex",
    alignItems: "center"
  },
  iconRoundContainer: {
    marginLeft:"20px",
    padding:"6px",
    borderRadius:"50%",
    border: "2px solid black",
    display:"flex",
    alignItems:"center",
    justifyContent:"center",
  }
}

function Card2(props) {
  const { classes, title, intro, linkTo } = props;
  return (
    <a className={classes.link} href={linkTo} target="_blank">

      <div className={classes.container}>

        <img className={classes.bgImage} src="" />

        <div className={classes.blurBox}></div>

        <div className={classes.content}>

          <div className={classes.iconContainer}>
            <h1 className={classes.h1}>{title}</h1>

            <div className={classes.iconRoundContainer}>
              <Icon
                className="fas fa-arrow-right"
                style={{ color: "black", fontSize: "16px", opacity: "1" }}
              />
            </div>

          </div>

          <div className={classes.iconContainer}>
            <Icon
              className="fab fa-medium-m"
              style={{ color: "black", fontSize: "16px", paddingRight: "8px", opacity: "1" }}
            />
            <p className={classes.p1}>{intro}</p>
          </div>
        </div>

      </div>


    </a>
  );
}

export default withStyles(styles)(Card2);
