import React from 'react'
import { withStyles } from "@material-ui/styles";

//
import ExpandableImage from "./ExpandableImage";
//Colors and Fonts
import Colors from "../Constant/Colors";
import Fonts from "../Constant/Fonts";

const styles = {
  root: {
    padding: "48px 64px",
    marginTop: "48px",
    boxSizing: "border-box",
    borderTop: `1px solid ${Colors.grey3}`,
  },
  wrapper: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "flex-end",
  },
  subTitle: {
    ...Fonts.l2,
    width: "300px",
  },
  p1: {
    ...Fonts.p1,
    width: "680px",
  },
  overFlow: {
    width: "100%",
    height: "680px",
    border: `1px solid ${Colors.grey3}`,
    borderRadius: "8px",
    overflow: "hidden"
  },
  overflowP1: {
    ...Fonts.p4,
    color: Colors.black2,
    textAlign: "center",
    backgroundColor: Colors.grey3,
    padding: "16px",
    borderRadius: "8px",
    opacity: "0.5"
  },
  overflowContainer: {
    marginTop: "48px"
  }
};

function ProjectContent(props) {
  const { classes, subTitle, copy, url, isImage } = props;
  return (
    <div className={classes.root}>

      <div className={classes.wrapper}>
        <h2 className={classes.subTitle}>{subTitle}</h2>
        <p className={classes.p1}>{copy}</p>
      </div>

      {isImage === "true" && <ExpandableImage image={url} />}
      {isImage === "false" && <div className={classes.overflowContainer}>
        <p className={classes.overflowP1}>Try zoom in/out, or click on the flow (FullScreen Allowed) </p>
        <iframe
          className={classes.overFlow}
          src={url}
          scrolling="no"
          allowfullscreen="true"
        />
      </div>}


    </div>
  )
}


export default withStyles(styles)(ProjectContent)
