import React from 'react'
import { withStyles } from "@material-ui/styles";
//
import Colors from "../Constant/Colors";

const styles = {
  link: {
    textDecoration: "none",
    color: Colors.black1,
  },
  root: {
    boxSizing: "border-box",
    // background: Colors.grey3,

    padding: "8px 8px",
    display: "flex",
    alignItems: "center",
    borderRadius: "8px",
    marginTop: "8px",
    "&:hover":{
      background: Colors.grey3,
    }
  },
  avatarContainer: {
    width: "40px",
    height: "40px",
    borderRadius: "50%",
    background: "white",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    overflow: "hidden",
    "& img": {
      width: "40px"
    }
  },
  info: {
    paddingLeft: "8px",
    "& p": {
      margin: "0px",
      fontSize: "14px",
      fontWeight: "500",
      letterSpacing: "-0.003em",
    }
  }
}

function TeamMember(props) {
  const { classes } = props;

  return (
    <a className={classes.link} href="https://www.linkedin.com/in/chenghan-ke/">

      <div className={classes.root}>

        <div className={classes.avatarContainer}>
          <img src="https://images.unsplash.com/photo-1534528741775-53994a69daeb?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1300&q=80" />
        </div>

        <div className={classes.info}>
          <p className={classes.name}>Sam Li</p>
          <p>Senior Product Manager</p>
        </div>
      </div>

    </a>


  )
}

export default withStyles(styles)(TeamMember);
