import React from 'react';
import { Link } from "react-router-dom";
import { withStyles } from "@material-ui/styles";
import Icon from "@material-ui/core/Icon";
import { IconButton } from '@material-ui/core';

const styles = {
  footer: {
    padding: "120px 24px",
    width: "100%",
    boxSizing: "border-box",
  },
  section: {
    display: "flex",
  },
  socialMedias: {
    display: "flex",
    alignItems: "center",
  },
  link: {
    opacity: "0.5",
    "&:hover": {
      opacity: "1",
      transition: "0.3s ease-in-out"
    }
  },
  email: {
    background: "rgba(255,255,255,0.15)",
    padding: "0px 24px",
    height: "40px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: "6px",
  },
  p1: {
    fontSize: "14px",
    fontWeight: "600",
    color: "white",
    opacity: "0.5"
  },
  copyRight: {
    borderTop: "1px solid rgba(255,255,255,0.3)", 
    padding:"16px 0px",
    width:"100%"
  },
  p2: {
    fontSize: "14px",
    fontWeight: "100",
    color: "white",
  }
}

function Footer(props) {
  const { classes } = props;
  return (
    <div>
      <div className={classes.footer}>
        <div className={classes.section}>
          <div className={classes.socialMedias}>
            <a className={classes.link} href="https://www.linkedin.com/in/chenghan-ke/" target="_blank"><Icon
              className="fab fa-linkedin-in"
              style={{ color: "white", paddingRight: "24px" }}
            /></a>
            <a className={classes.link} href="https://www.linkedin.com/in/chenghan-ke/" target="_blank"><Icon
              className="fab fa-github"
              style={{ color: "white", paddingRight: "24px" }}
            /></a>
            <a className={classes.link} href="https://www.linkedin.com/in/chenghan-ke/" target="_blank"><Icon
              className="fab fa-instagram"
              style={{ color: "white", paddingRight: "24px" }}
            /></a>
            <a className={classes.link} href="https://www.linkedin.com/in/chenghan-ke/" target="_blank"><Icon
              className="fab fa-dribbble"
              style={{ color: "white", paddingRight: "24px" }}
            /></a>
          </div>
          <div className={classes.email}>
            <p className={classes.p1}>hankkedesign@gmail.com</p>
          </div>
        </div>
      </div>

      <div className={classes.copyRight}>
        <p className={classes.p2}>hankkedesign © 2020 </p>
      </div>

    </div>
  )
}

export default withStyles(styles)(Footer);
