import React, { useState } from 'react'
import { withStyles } from "@material-ui/styles";
//
import Colors from "../Constant/Colors";

const styles = {
  root: {
    width: "100%",
    height: "80%",
    borderRadius: "8px",
    overflow: "hidden",
    background: "none",
    marginTop: "48px",
    alignItems: "center",
    justifyContent: "center",
    "&:hover": {
      cursor: "zoom-in"
    }
  },
  image: {
    width: "100%",
  },
  imageEnlarge: {
    position: "absolute",
    width: "100%",
    height: "100vh",
    top: "0px",
    left: "0px",
    zIndex: "20",
    background: "black",
    display: "flex",
    alignItems: "center",
    opacity:"0",
    "& img": {
      width: "100%",
    }
  },
  showImageEnlarge:{
    opacity:"1",
    cursor:"zoom-out"
  }

};

function ExpandableImage(props) {

  const [expand, setExpand] = useState(false);
  const { classes, image } = props;


  return (
    <div className={classes.root}>
      {/* Overlay */}

      <div className={`${classes.imageEnlarge} ${expand && classes.showImageEnlarge}`} >
        <img src={image} />
      </div>


      {/* Normal */}
      <img
        className={classes.image}
        src={image}
        onClick={() => setExpand(true)}
      />

    </div>

  )
}

export default withStyles(styles)(ExpandableImage);
