import React from 'react';
import { withStyles } from "@material-ui/styles";
import { Link } from "react-router-dom";
import Fonts from "../Constant/Fonts";
import Colors from "../Constant/Colors";
import Icon from "@material-ui/core/Icon";



const styles = {
  link: {
    textDecoration: "none",
    color: Colors.black2,
  },
  smallCard: {
    position: "relative",
    width: "100%",
    height: "540px",
    background: "white",
    borderRadius: "16px",
    boxSizing: "border-box",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    textAlign: "center",
    overflow: "hidden",
    '&:hover': {
      transform: "scale(1.02)",
      transition: "0.3s ease-in-out",
      boxShadow: "0px 6px 29px 8px rgba(89, 89, 89, 0.6)",
      cursor: "pointer",
      "& button": {
        transition: "0.5s ease-in-out",
        transform: "translateY(-4px)",
        boxShadow: "0px 6px 29px 8px rgba(89, 89, 89, 0.3)",
      }
    }
  },
  content: {
    boxSizing: "border-box",
    padding: "32px"
  },
  h2: {
    ...Fonts.h2
  },
  p2: {
    ...Fonts.p2
  },
  button: {
    height: "40px",
    width: "40px",
    borderRadius: "20px",
    border: "none",
    opacity: "0.8",
    backgroundColor: "rgba(0,0,0,0.1)",
    color: "black",
    marginTop: "24px"
  }
}

function SmallCard(props) {
  const { classes, title, intro } = props;
  return (
    <Link to="/projectDetail/:projectId" className={classes.link}>
      <div className={classes.smallCard}>
        <div className={classes.content}>
          <h2 className={classes.h2}>{title}</h2>
          <p className={classes.p2}>{intro}</p>
          <button className={classes.button}>
            <Icon
              className="fas fa-chevron-left"
              style={{ color: "white", paddingRight: "6px", fontSize: "16px" }}
            />
          </button>
        </div>
      </div>
    </Link>
  )
};

export default withStyles(styles)(SmallCard);
