import React from 'react';
import { withStyles } from "@material-ui/styles";
import { Link } from "react-router-dom";
import Fonts from "../Constant/Fonts";
import Colors from "../Constant/Colors";
import Icon from "@material-ui/core/Icon";


const styles = {
  link: {
    textDecoration: "none",
    color: "white",
  },
  root: {
    position: "relative",
    width: "100%",
    height: "100%",
    background: props => props.backgroundColor,
    borderRadius: "16px",
    boxSizing: "border-box",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",

    overflow: "hidden",
    '&:hover': {
      transform: "translateY(-10px)",
      transition: "0.3s ease-in-out",
      boxShadow: `0px 6px 29px 8px ${Colors.black1}`,
      cursor: "pointer",
      "& button": {
        transition: "0.5s ease-in-out",
        transform: "translateY(-4px)",
        boxShadow: "0px 0px 29px 8px rgba(0, 0, 0, 0.2)",
      }
    }
  },

  content: {
    boxSizing: "border-box",
    padding: "16px",
    background:Colors.black1,
  },

  h2: {
    ...Fonts.h2
  },
  p2: {
    paddingTop: "8px",
    ...Fonts.p2
  },
  button: {
    height: "32px",
    width: "32px",
    borderRadius: "20px",
    border: "none",
    opacity: "0.8",
    backgroundColor: "rgba(0,0,0,0.1)",
    color: "black",
    marginTop: "16px",
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center"
  },
  imageContainer: {
    width: "100%",
  },
  frontImage: {
    width: "100%",
  }
}

function SmallCard(props) {
  const { classes, title, intro, frontImage, backgroundColor } = props;
  return (
    <Link to={`/projectDetail/${title}`} className={classes.link}>
      <div className={classes.root}>

        <div className={classes.imageContainer}>
          <img className={classes.frontImage} src={frontImage} />
        </div>

        <div className={classes.content}>
          <h2 className={classes.h2}>{title}</h2>
          <p className={classes.p2}>{intro}</p>
          {/* <button className={classes.button}>
            <Icon
              className="fas fa-chevron-right"
              style={{ color: "white", fontSize: "14px" }}
            />
          </button> */}

        </div>
      </div>
    </Link>
  )
};

export default withStyles(styles)(SmallCard);
