import React from 'react';
import { withStyles } from "@material-ui/styles";
import Colors from "../Constant/Colors";
import Fonts from "../Constant/Fonts";
import { Icon } from '@material-ui/core';

//Define Styles here↓
const styles = {
  link: {
    textDecoration: "none",
    color: Colors.black1
  },
  root: {
    width: "100%",
    background: Colors.black2,
    boxSizing: "border-box",
    padding: "23px 0px",
    margin: "0xp auto",
    "&:hover": {
      cursor: "pointer"
    }
  },
  container: {
    width: "1080px",
    height: "320px",
    margin: "0px auto",
    position: "relative",
    borderRadius: "8px",
    boxSizing: "border-box",
    background: "white",
    overflow: "hidden"
  },
  content: {
    boxSizing: "border-box",
    width: "50%",
    height: "100%",
    paddingLeft: "10%",
    position: "absolute",
    display: "flex",
    justifyContent: "center",
    flexDirection: "column",
  },
  h1: {
    ...Fonts.h1
  },
  p1: {
    ...Fonts.p1
  },
  blurBox:{
   width:"30%",
   height:"100%",
   background:Colors.greybg,
   position: "absolute",
   boxShadow:`500px 0px 85px 87px ${Colors.greybg}`
  },
  bgImage: {
    position: "absolute",
    width: "100%",
    top: "0px",
    left: "500px",
  },
  iconContainer: {
    paddingTop: "8px",
    display:"flex",
    alignItems:"center"
  }
}

function Card(props) {
  const { classes, title, intro } = props;
  return (
    <a className={classes.link} href="https://uxdesign.cc/cheat-sheets-ui-terms-cadf9dc49689" target="_blank">
      <div className={classes.root}>

        <div className={classes.container}>

          <img className={classes.bgImage} src="https://miro.medium.com/max/2000/1*tXAVkpZZw4sMeyLVpvCZbg.png" />
          <div className={classes.blurBox}></div>

          <div className={classes.content}>
            <h1 className={classes.h1}>{title}</h1>

            <div className={classes.iconContainer}>
              <p className={classes.p1}>{intro}</p>
              <Icon
                className="fas fa-external-link-alt"
                style={{ color: "black", fontSize: "14px", paddingLeft:"24px",opacity:"0.6" }}
              />
            </div>
          </div>

        </div>

      </div>
    </a>
  );
}

export default withStyles(styles)(Card);
