import React from 'react'
import { withStyles } from "@material-ui/styles";

//Colors and Fonts
import Colors from "../Constant/Colors";
import Fonts from "../Constant/Fonts";

const styles = {
  root: {
    padding: "48px 48px",
    marginTop: "48px",
    boxSizing: "border-box",
    borderTop: `2px solid ${Colors.ongrey}`,
  },
  subTitle: {
    ...Fonts.h2
  },
  p1: {
    ...Fonts.p1
  },
  imageContainer: {
    width: "100%",
    height: "300px",
    borderRadius: "8px",
    overflow: "hidden",
    background:"white",
    marginTop:"24px"
  }
};

function ProjectContent(props) {
  const { classes } = props;
  return (
    <div className={classes.root}>
      <h2 className={classes.subTitle}>Secondary Title</h2>
      <p className={classes.p1}>An overview of my design process. I got a task from PM, Sam Li, in which talked about adding a Cash on Delivery (COD) as a new payment method for users in Malaysia. I then scheduled a meeting with Marketing Manager, Brandon, who won this project, and talk more details about business requirements. To better understand what is the current COD experience in SEA, I conducted user study and gained insights from users.</p>
      <div className={classes.imageContainer}>
        <img  />
      </div>
    </div>
  )
}


export default withStyles(styles)(ProjectContent)
