import React from 'react'
import { withStyles } from "@material-ui/styles";
//
import Colors from "../Constant/Colors";

const styles = {
  link: {
    textDecoration: "none",
    color: "black",
  },
  root: {
    boxSizing: "border-box",
    width: "120px",
    background: "grey",
    border: "1px solid yellow",
    padding: "20px 16px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    borderRadius: "8px",
    marginLeft: "8px",
  },
  avatarContainer: {
    width: "40px",
    height: "40px",
    borderRadius: "50%",
    background: "white",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    overflow: "hidden",
    "& img": {
      width: "40px"
    }
  },
  info: {
    border: "1px solid blue",
    marginTop: "16px",
    "& p": {
      margin: "0px",
      textAlign: "center",
      fontSize: "12px"
    }
  }
}

function TeamMember(props, Colors) {
  TeamMember.defaultProps = { colors: Colors };
  const { classes } = props;

  return (
    <a className={classes.link} href="https://www.linkedin.com/in/chenghan-ke/">

      <div className={classes.root}>
        <div className={classes.avatarContainer}>
          <img src="https://images.unsplash.com/photo-1534528741775-53994a69daeb?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1300&q=80" />
        </div>
        <div className={classes.info}>
          <p>Sam Li</p>
          <p>Senior Product Manager</p>
        </div>
      </div>

    </a>


  )
}

export default withStyles(styles)(TeamMember);
