import React from 'react'
import { withStyles } from "@material-ui/styles"
import Icon from "@material-ui/core/Icon";
import Colors from "../Constant/Colors";
import Fonts from "../Constant/Fonts";

//Define Styles ↓
const styles = {
  root: {
    background: Colors.black2,
    boxSizing: "border-box"
  },
  container: {
    width: "1080px",
    height: "50vh",
    margin: "0px auto",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    boxSizing: "border-box"
  },
  h1: {
    ...Fonts.s1,
    padding: "0px 24px",
    color: "white",
    textAlign: "center"
  },
  p1: {
    color: Colors.ongrey,
    fontSize:"20px",
    fontWeight:"500",
    paddingTop: "24px",
    textAlign: "center"
  },
  socialMedias: {
    paddingTop: "24px",
    display: "flex",
    alignItems: "center",
  },
  link: {
    opacity: "0.5",
    "&:hover": {
      opacity: "1",
      transition: "0.3s ease-in-out"
    }
  },
}

function Hero(props) {
  const { classes, slogan, content, socialAccount } = props
  return (
    <div className={classes.root}>
      <div className={classes.container}>
        <h1 className={classes.h1}>{slogan}</h1>
        <p className={classes.p1}>{content}</p>
        {socialAccount &&
          <div className={classes.socialMedias}>
            <a className={classes.link} href="https://www.linkedin.com/in/chenghan-ke/" target="_blank"><Icon
              className="fab fa-linkedin-in"
              style={{ color: "white", paddingRight: "24px" }}
            /></a>
            <a className={classes.link} href="https://github.com/hke-cheng" target="_blank"><Icon
              className="fab fa-github"
              style={{ color: "white", paddingRight: "24px" }}
            /></a>
            <a className={classes.link} href="https://www.instagram.com/chenghanke/" target="_blank"><Icon
              className="fab fa-instagram"
              style={{ color: "white", paddingRight: "24px" }}
            /></a>
            <a className={classes.link} href="https://www.linkedin.com/in/chenghan-ke/" target="_blank"><Icon
              className="fab fa-dribbble"
              style={{ color: "white", paddingRight: "24px" }}
            /></a>
            <a className={classes.link} href="https://medium.com/@hankkechenghan" target="_blank"><Icon
              className="fab fa-medium-m"
              style={{ color: "white", paddingRight: "24px" }}
            /></a>
          </div>
        }
      </div>

    </div>
  )
}

export default withStyles(styles)(Hero);
