import Colors from "../Constant/Colors";
import Fonts from "../Constant/Fonts";

export default {
  root: {
    boxSizing: "border-box"
  },

  container: {
    // width: "1080px",
    height: "40vh",
    margin: "0px 10vw",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    boxSizing: "border-box"
  },

  '@media (min-width: 1024px)': {
    container: {
      // width: "1080px",
      height: "40vh",
      margin: "0px 10vw",
    }
  },

  '@media (max-width: 768px)': {
    container: {
      // width: "1080px",
      height: "40vh",
      margin: "0px 10vw",
    }
  },

  h1: {
    ...Fonts.s1,
    color: "white",
    textAlign: "center"
  },

  h2: {
    ...Fonts.h2,
    color: Colors.blue,
    paddingTop: "24px",
    textAlign: "center"
  },
}
