import Colors from "../Constant/Colors";
import Fonts from "../Constant/Fonts";

export default {
  root: {
    background: Colors.black2,
    boxSizing: "border-box"
  },
  container: {
    width: "980px",
    height: "40vh",
    margin: "0px auto",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    boxSizing: "border-box"
  },

  h1: {
    ...Fonts.s1,
    color: "white",
    textAlign: "center"
  },

  h2: {
    ...Fonts.h2,
    color: Colors.yellow,
    paddingTop: "24px",
    textAlign: "center"
  },
}
