import React from 'react';
import { withStyles } from "@material-ui/styles";
import { Link } from "react-router-dom";
import Fonts from "../Constant/Fonts";
import Colors from "../Constant/Colors";


const styles = {
  link: {
    textDecoration: "none",
    color: props => props.textColor,
  },
  root: {
    position: "relative",
    width: "100%",
    height: "460px",
    background: props => props.backgroundColor,
    borderRadius: "8px",
    boxSizing: "border-box",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",

    overflow: "hidden",
    '&:hover': {
      transform: "translateY(-10px)",
      transition: "0.3s ease-in-out",
      boxShadow: `0px 6px 29px 8px ${Colors.black1}`,
      cursor: "pointer",
    }
  },

  content: {
    boxSizing: "border-box",
    padding: "32px",
    margin: "0px",
    position: "relative",
    zIndex: "20",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    textAlign: "center"
  },

  h2: {
    ...Fonts.h2,
    textAlign: "center"
  },
  p2: {
    paddingTop: "8px",
    ...Fonts.p2,
    textAlign: "center",
    opacity: "0.6"
  },

  imageContainer: {
    width: "260px",
    position: "absolute",
    bottom: "-6px",
    alignItems:"center",
    justifyContent:"center",
    border:"1px solid black"
  },
  frontImage: {
    width: "100%",
  },

  // logoContainer: {
  //   width: "32px",
  //   height: "32px",
  //   overflow: "hidden",
  //   borderRadius: "50%",
  //   display: "flex",
  //   alignItems: "center",
  //   justifyContent: "center",
  //   position: "absolute",
  //   top: "8px",
  //   right: "8px"
  // },
  // logo: {
  //   width: "26px"
  // }
}

function SmallCard(props) {
  const { classes, title, intro, frontImage, program } = props;
  return (
    <Link to={`/projectDetail/${title}`} className={classes.link}>
      <div className={classes.root}>

        <div className={classes.content}>

          {/* <div className={classes.logoContainer}>
            <img className={classes.logo} src={program} />
          </div> */}

          <h2 className={classes.h2}>{title}</h2>
          <p className={classes.p2}>{intro}</p>
        </div>

        <div className={classes.imageContainer}>
          <img className={classes.frontImage} src={frontImage} />
        </div>
      </div>
    </Link>
  )
};

export default withStyles(styles)(SmallCard);
